con:config.script:
import groovy.json.*

// get the response from the first request using its name
def response = context.expand('${Authorize REST Request#Response}')
// parse it
def json = new JsonSlurper().parseText(response)
log.info json.transaction_id
// get the json value an set it as property in testCase
context.testCase.setPropertyValue("transaction_id",json.transaction_id.toString())
